class prog15
{
 public static void main(String	 args[])
 {
  int age=10;
  if(age>=60)
  {
   String gender="female";
   if(gender=="female"	)
    System.out.println("retirement amount 30000 provided  because you are female senior citizen ");
   else
    System.out.println("retirement amount 35000 provided  because you are male senior citizen");
  }
  else if(age>=18)
  {
   String qualification="UG";
   if(qualification=="UG")
    System.out.println("scholorship amount 30000 provided  because you are under graduate major ");
   else if(qualification=="PG")
   System.out.println("scholorship amount 40000 provided  because you are post graduate major ");
   else
    System.out.println("scholorship amount 20000 provided  because you are major but neither UG nor PG");
  }
  else
  {
   System.out.println("you are not eligible for scholorhip and retirement because you are a minor ");
  }
 }
}
    
																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																																												